import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './RecipeDetail.css';

const RecipeDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isLiked, setIsLiked] = useState(false);

  useEffect(() => {
    fetchRecipe();
  }, [id]);

  const fetchRecipe = async () => {
    try {
      const response = await axios.get(`/api/recipes/${id}`);
      setRecipe(response.data);

      // Ê£ÄÊü•ÂΩìÂâçÁî®Êà∑ÊòØÂê¶Â∑≤ÁÇπËµû
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      if (user._id) {
        setIsLiked(response.data.likes.includes(user._id));
      }
    } catch (error) {
      setError('ËèúË∞±‰∏çÂ≠òÂú®ÊàñÂ∑≤Ë¢´Âà†Èô§');
    } finally {
      setLoading(false);
    }
  };

  const handleLike = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const response = await axios.post(`/api/recipes/${id}/like`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRecipe(response.data);
      setIsLiked(!isLiked);
    } catch (error) {
      console.error('ÁÇπËµûÂ§±Ë¥•:', error);
    }
  };

  if (loading) {
    return <div className="text-center">Âä†ËΩΩ‰∏≠...</div>;
  }

  if (error) {
    return <div className="text-center error-message">{error}</div>;
  }

  if (!recipe) {
    return <div className="text-center">ËèúË∞±‰∏çÂ≠òÂú®</div>;
  }

  return (
    <div className="recipe-detail">
      <div className="recipe-header">
        <div className="recipe-image">
          {recipe.image ? (
            <img src={recipe.image} alt={recipe.title} />
          ) : (
            <div className="placeholder-image">üçΩÔ∏è</div>
          )}
        </div>
        <div className="recipe-info">
          <h1>{recipe.title}</h1>
          <p className="recipe-description">{recipe.description}</p>
          <div className="recipe-meta">
            <span>‚è±Ô∏è {recipe.cookingTime}ÂàÜÈíü</span>
            <span>üë• {recipe.servings}‰∫∫‰ªΩ</span>
            <span>üìä {recipe.difficulty}</span>
            <span>üçΩÔ∏è {recipe.cuisine}</span>
          </div>
          <div className="recipe-author">
            <span>‰ΩúËÄÖ: {recipe.author?.username || 'Êú™Áü•'}</span>
            <span>ÂèëÂ∏ÉÊó∂Èó¥: {new Date(recipe.createdAt).toLocaleDateString()}</span>
          </div>
          <div className="recipe-actions">
            <button 
              onClick={handleLike} 
              className={`btn ${isLiked ? 'btn-primary' : 'btn-secondary'}`}
            >
              {isLiked ? '‚ù§Ô∏è Â∑≤ÁÇπËµû' : 'ü§ç ÁÇπËµû'} ({recipe.likes.length})
            </button>
          </div>
        </div>
      </div>

      <div className="recipe-content">
        <div className="ingredients-section">
          <h2>È£üÊùê</h2>
          <ul className="ingredients-list">
            {recipe.ingredients.map((ingredient, index) => (
              <li key={index}>
                <span className="ingredient-name">{ingredient.name}</span>
                <span className="ingredient-amount">{ingredient.amount}</span>
              </li>
            ))}
          </ul>
        </div>

        <div className="instructions-section">
          <h2>Âà∂‰ΩúÊ≠•È™§</h2>
          <ol className="instructions-list">
            {recipe.instructions.map((instruction, index) => (
              <li key={index}>{instruction}</li>
            ))}
          </ol>
        </div>

        {recipe.tags && recipe.tags.length > 0 && (
          <div className="tags-section">
            <h3>Ê†áÁ≠æ</h3>
            <div className="tags">
              {recipe.tags.map((tag, index) => (
                <span key={index} className="tag">{tag}</span>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RecipeDetail;
